import Vao from '../renderer/helpers/vao';
import { createProgram } from '../renderer/helpers/program';
import { getContext } from '../session';

const programs = {};

class Material {

    constructor() {
        this.defines = {};
        this.attributes = {};
        this.uniforms = {};

        this.vertex = '';
        this.fragment = '';

        this.vao = new Vao();
        this.program = null;
    }

    init() {
        const gl = getContext();

        // 1) create shaders dynamically
        this.vertex = this.generateVertexShader();
        this.fragment = this.generateFragmentShader();

        // 2) create program
        const key = `shader-${this.type}`;
        if (programs[key] === undefined) {
            const program = createProgram(gl, this.vertex, this.fragment);
            programs[key] = program;
        }
        this.program = programs[key];

        // 2) binds buffers
        for (let prop in this.attributes) {
            const current = this.attributes[prop];

            const location = gl.getAttribLocation(this.program, prop);

            const buffer = gl.createBuffer();
            gl.bindBuffer(gl.ARRAY_BUFFER, buffer);
            gl.bufferData(gl.ARRAY_BUFFER, current.value, gl.STATIC_DRAW);
            // unbind?

            let size;
            switch (current.type) {
                case 'vec3':
                    size = 3;
                    break;
                case 'vec2':
                    size = 3;
                    break;
                default:
                    size = 1;
            }

            Object.assign(current, {
                location,
                buffer,
                size,
                // value?
            });
        }

        if (this.indices) {
            this.indexBuffer = gl.createBuffer();
        }

        // 3) bind vao
        this.vao.bind();

        // 4) bind attributes
        this.bind();

        // 5) unbind vao
        this.vao.unbind();
        // unbind attributes?
    }

    destroy() {
        // destroy buffers
    }

    generateVertexShader() {
        return `#version 300 es
            in vec3 a_position;

            void main() {
                gl_Position = vec4(a_position, 1.0);
            }
        `;
    }

    generateFragmentShader() {
        return `#version 300 es
            precision highp float;
            precision highp int;

            out vec4 outColor;

            void main() {
                outColor = vec4(1.0, 0.0, 1.0, 1.0);
            }
        `;
    }

    bind() {
        const gl = getContext();
        Object.keys(this.attributes).forEach(key => {
            const { location, buffer, size } = this.attributes[key];
            if (location !== -1) {
                gl.enableVertexAttribArray(location);
                gl.bindBuffer(gl.ARRAY_BUFFER, buffer);
                gl.vertexAttribPointer(location, size, gl.FLOAT, false, 0, 0);
                gl.bindBuffer(gl.ARRAY_BUFFER, null);
            }
        });

        if (this.indices) {
            gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, this.indexBuffer);
            gl.bufferData(gl.ELEMENT_ARRAY_BUFFER, this.indices, gl.STATIC_DRAW);
        }
    }

    update() {
        // update uniforms / buffers
    }

    unbind() {
        const gl = getContext();
        gl.bindBuffer(gl.ELEMENT_ARRAY_BUFFER, null);
    }

}

export default Material;

import Object3 from './object3';
import { getContext } from '../session';

class Model extends Object3 {

    constructor(geometry, material) {
        super();

        this.geometry = {
            positions: new Float32Array(geometry.positions),
            indices: new Uint16Array(geometry.indices),
            normals: new Float32Array(geometry.normals),
            uvs: new Float32Array(geometry.uvs),
        };
        this.material = material;

        // merge geometry to material attributes
        Object.assign(this.material.attributes, {
            a_position: {
                type: 'vec3',
                value: this.geometry.positions,
            },
            a_normal: {
                type: 'vec3',
                value: this.geometry.normals,
            },
            a_uv: {
                type: 'vec2',
                value: this.geometry.uvs,
            },
        });

        // pass indices to material so we can bind buffers
        Object.assign(this.material, { indices: this.geometry.indices });
    }

    destroy() {
        // destroys buffers
        this.material.destroy();
    }

    update() {
        const gl = getContext();

        // everything is bind
        // update material attributes or uniforms
        this.material.update();

        // draw
        gl.drawElements(gl.TRIANGLES, this.geometry.indices.length, gl.UNSIGNED_SHORT, 0);
    }

}

export default Model;

import { vec3, mat4, quat } from 'gl-matrix';
import Vector3 from './vector3';

let uuid = 0;
let axisAngle = 0;
let quaternionAxisAngle = vec3.create();

class Object3 {

    constructor() {
        this.uid = uuid++;

        this.position = new Vector3();
        this.rotation = new Vector3();
        this.scale = new Vector3(1, 1, 1);
        this.quaternion = quat.create();

        this.parent = null;
        this.children = [];

        this.localMatrix = mat4.create();
        this.modelMatrix = mat4.create();
    }

    updateMatrices() {
        mat4.identity(this.localMatrix);
        mat4.identity(this.modelMatrix);
        quat.identity(this.quaternion);

        if (this.parent) {
            mat4.copy(this.localMatrix, this.parent.modelMatrix);
            mat4.multiply(this.modelMatrix, this.modelMatrix, this.localMatrix);
        }

        mat4.translate(this.modelMatrix, this.modelMatrix, this.position.data);
        quat.rotateX(this.quaternion, this.quaternion, this.rotation.x);
        quat.rotateY(this.quaternion, this.quaternion, this.rotation.y);
        quat.rotateZ(this.quaternion, this.quaternion, this.rotation.z);
        axisAngle = quat.getAxisAngle(quaternionAxisAngle, this.quaternion);
        mat4.rotate(this.modelMatrix, this.modelMatrix, axisAngle, quaternionAxisAngle);
        mat4.scale(this.modelMatrix, this.modelMatrix, this.scale.data);
    }

}

export default Object3;

import { mat4 } from 'gl-matrix';
import Object3 from './object3';

class Scene extends Object3 {

    constructor() {
        super();
        this.lights = [];

        this.modelViewMatrix = mat4.create();
    }

    addModel(model) {
        model.parent = this;
        this.children.push(model);
    }

    removeModel(model) {
        const index = this.children.indexOf(model);
        if (index !== -1) {
            model.destroy();
            this.children.splice(index, 1);
        }
    }

    traverse(object) {
        if (object === undefined) {
            object = this;
        }

        for (let i = 0; i < object.children.length; i += 1) {
            this.traverse(object.children[i]);
        }

        if (object.parent === null) {
            return;
        }

        object.updateMatrices();
    };

}

export default Scene;

import { vec3 } from 'gl-matrix';

class Vector3 {

    constructor(x = 0, y = 0, z = 0) {
        this.data = vec3.fromValues(x, y, z);
    }

    set x(value) {
        vec3.set(this.data, value, this.data[1], this.data[2]);
    }

    get x() {
        return this.data[0];
    }

    set y(value) {
        vec3.set(this.data, this.data[0], value, this.data[2]);
    }

    get y() {
        return this.data[1];
    }

    set z(value) {
        vec3.set(this.data, this.data[0], this.data[1], value);
    }

    get z() {
        return this.data[2];
    }

}

